#
# Table structure for table 'Relaciones'
#

DROP TABLE IF EXISTS Relaciones;
CREATE TABLE Relaciones (
  id_relacion int(5) NOT NULL auto_increment,
  tabla_origen varchar(50) default NULL,
  campo_origen varchar(50) default NULL,
  tabla_destino varchar(50) default NULL,
  campo_destino varchar(50) default NULL,
  tipo char(3) default NULL,
  PRIMARY KEY (id_relacion)
) TYPE=MyISAM;

